import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "../Css/BeerView.css";
import axios from "axios";


class BeerViewSingle extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount() {
        //Great hack for making "this" work
        var self = this;
        axios.get("https://ih-beers-api.herokuapp.com/beers")
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render() {
        console.log(this.props.match.params)


        let singleBeer = this.state.response.find( function Nerr (beer) {
            beer._id === this.props.match.params
        });



        return(
            <div className="BeerView">
                {singleBeer}
            </div>
        )
    }
}

export default BeerViewSingle;
