import React, {Component} from 'react';
import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import MapMarker from "../Components/MapMarker";
// import CreateMarker from "../Components/CreateMarker";
import TopBar from "../Components/TopBar";
import Navbar from "../Components/Navbar";
import axios from "axios";


class Map extends Component {
    constructor(props) {
        super(props);

        this.state = {
            markers: [],
            latitude: 37.7577,
            longitude: -122.4376,
            showPopup:{
                showPopup: true
            },
            viewport: {
                width: "100vw",
                height: "100vh",
                latitude: 37.7577,
                longitude: -122.4376,
                zoom: 8
            }
        }; 
        this.componentDidMount = this.componentDidMount.bind(this);
    }
    componentDidMount() {
        // Setting the APIURL
        let URL = `${process.env.REACT_APP_API}/marker/all-markers`;
        axios.get(URL)
        .then( (response)=>{
            debugger
            this.setState({markers: response.data})            
        })
        .catch((error)=> {
            debugger
        });
    }

    getLatLng(event)  {
        console.log(event.lngLat)
    }

    render() {
        debugger
        console.log(this.state.response)
        let markers = this.state.markers.map(marker => (
            <Marker latitude={parseInt(marker.lat)} longitude={parseInt(marker.lng)} />
        ))
        return (
            <div>     
                <Navbar/>
                <TopBar />
                <ReactMapGL 
                    mapboxApiAccessToken="pk.eyJ1IjoibWFyaXVzZ3JvbmRhaGwiLCJhIjoiY2p6OHZpb2JpMDMwNTNkbzJlbnJyb2lkdiJ9.zTQFrIU4__uaAPGzAUQSFw" 
                    {...this.state.viewport}
                    onViewportChange={(viewport) => this.setState({viewport})}
                >                
                    {}
                </ReactMapGL>
            </div>


    );
  }
}

export default Map;
