import React, {Component} from "react";
import "../Css/MapMarker.css"
import axios from "axios";

class MapMarker extends Component {
    constructor(props) {
        super(props);

        this.state = {
            weather: [],
        }; 
        this.componentDidMount = this.componentDidMount.bind(this);
    }
    componentDidMount() {
        // Setting the APIURL 
        let DarkSkyKey = "89d15b087b4a2998b07b5e424f151ecc/";
        let latitude = this.props.lat;
        let longitude = this.props.lng;
        let WeatherURL  = "https://api.darksky.net/forecast/" + 
                            DarkSkyKey + 
                            latitude + 
                            "," + longitude;

        axios.get(WeatherURL)
        .then( (response)=>{
            this.setState({weather: response.data})   
        })

        .catch((error)=> {
            return error
        });
    }
    render() {
        console.log(this.state.weather)
        let weather = this.state.weather.map(weather => (
            <div className="weather">
                <h1>{weather.currently.summary}</h1>
            </div>
        ))
    return(
                <div className="MapMarker">
                    <h1>{this.props.name}</h1>
                    {weather}
                </div>


    )
    }
}

export default MapMarker;
