import React, {Component} from 'react';
import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import MapMarker from "../Components/MapMarker"
import CreateMarker from "../Components/CreateMarker";
import Navbar from "../Components/Navbar";
import axios from "axios"


class Map extends Component {

    constructor(props) {
        super(props);
        this.state = {
            response: []
        };
    }

        componentDidMount() {
            // Setting the APIURL
            let URL = `${process.env.REACT_APP_API}/marker/all-markers`;
            //Great hack for making "this" work
            var self = this;
            axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
        }

    getLatLng(event)  {
        console.log(event.lngLat)
      }

  render() {
    const TOKEN = "pk.eyJ1IjoibWFyaXVzZ3JvbmRhaGwiLCJhIjoiY2p6OHZpb2JpMDMwNTNkbzJlbnJyb2lkdiJ9.zTQFrIU4__uaAPGzAUQSFw";
    return (
        <div>
        <Navbar/>

            <ReactMapGL 
                onDblClick={this.getLatLng} 
                mapboxApiAccessToken={TOKEN} 
                {...this.state.viewport}
                onViewportChange={(viewport) => this.setState({viewport})}
            >
            <CreateMarker />
                <Marker longitude={8} latitude={52} onClick={this.handleClick}  offsetLeft={-20} offsetTop={-10}>
                    <MapMarker title={this.state.response.lat} desc="Lengre inngress om bla bla bla"/>
                </Marker>

            </ReactMapGL>
            </div>


    );
  }
}

export default Map;
