                    <MapMarker title={marker.spot_name} 
                            lng={marker.lng}
                            lat={marker.lng}        
                    />
            )})

    return (
        <div>     
            <Navbar/>
            <TopBar />
            <ReactMapGL 
                onDblClick={this.getLatLng} 
                mapboxApiAccessToken="pk.eyJ1IjoibWFyaXVzZ3JvbmRhaGwiLCJhIjoiY2p6OHZpb2JpMDMwNTNkbzJlbnJyb2lkdiJ9.zTQFrIU4__uaAPGzAUQSFw" 
                {...this.state.viewport}
            >                
                {Marker[0]}


            </ReactMapGL>
        </div>


    );
  }
}

export default Map;
