import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "../Css/BeerView.css";
import axios from "axios";


class BeerViewSingle extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount() {

        //Great hack for making "this" work
        var self = this;
        axios.get("https://ih-beers-api.herokuapp.com/beers/")
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render() {
        // Catching the params from URL
        let params = this.props.match.params.id

        var singleBeer = this.state.response.find(function(beer) {
            return beer._id === params
          });




        return(
            <div className="BeerView">
                {singleBeer}
            </div>
        )
    }
}

export default BeerViewSingle;
