import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "../Css/BeerView.css";
import axios from "axios";


class BeerViewSingle extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount() {
        //Great hack for making "this" work
        var self = this;
        axios.get("https://ih-beers-api.herokuapp.com/beers")
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render() {
        return(
            <div className="BeerView">
                <Link to={this.props.link}><h2>{this.props.title}</h2></Link>
                <img src={this.props.image} width="50"/>
                <p>{this.props.description}</p>
                <p>{this.props.created}</p>
                <Link to={this.props.link}>Read More</Link>
            </div>
        )
    }
}

export default BeerViewSingle;
