import React, { Component } from "react";
import "../Css/AddBeer.css";
import axios from "axios";
import qs from "querystring"; // used for parsing a javascript object in the right format (x-www-form-urlencoded)


class AddBeer extends Component {
    constructor(props) {

        super(props);
        this.state = { 
          tagline: "",
          first_brewed: "",
          brewers_tips: "",
          contributed_by: "",
          attenuation_level: "",
          name: ""
        }
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    handleFormSubmit = (e)=> {
        e.preventDefault(); // Disable defaut POST action
        debugger
        axios({
            method: "POST",
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data:  qs.stringify(this.state), // don't send data in json format
            url: `${process.env.REACT_APP_API}/beers/new`
        })
        .then((response)=> {

        })
        .catch((error)=> {
            console.log(error)
        })

    }

    render() {
        return (
            <div>
                <h1>Create your own Beer!</h1>
                <form className="FormContainer">
                    <div className="flexItem">
                        <label>Name:</label>
                        <input type="text" placeholder="Beername"></input>
                    </div>

                    <div className="flexItem">
                        <label>Tagline:</label>
                        <input type="text" placeholder="Tagline"></input>
                    </div>

                    <div className="flexItem">
                        <label>Describe the beer:</label>
                        <textarea 
                            type="text" 
                            placeholder="description"  
                            name="description" 
                            value={this.state.description} 
                            onChange={this.handleFormChange}></textarea>
                    </div>

                    <div className="flexItem">
                        <label>First brewed:</label>
                        <input type="text" placeholder="First brewingdate"></input>
                    </div>

                    <div className="flexItem">
                        <label>Brewers tips:</label>
                        <textarea type="text" placeholder="Tips from the Brewer"></textarea>
                    </div>

                    <div className="flexItem">
                        <label>Attenuationlevel:</label>
                        <input type="number" placeholder="0"></input>
                    </div>

                    <div className="flexItem">
                        <label>Added by:</label>
                        <input type="text" placeholder="Your name"></input>
                    </div>


                    <div className="flexItem">
                        <input type="submit" value="Submit" />
                    </div>

                </form>
            </div>

        )
    }
}

export default AddBeer
