import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import Fullscreen from "../Components/Fullscreen";
import axios from "axios";
import qs from "querystring";

class CreateSpot extends Component {
    constructor(props){
        super(props);

    this.state = {
        spot: {
            spot_name: "", 
            desc: "", 
            lat: "",
            lng: " ",
            img: " ",
            north: false,
            north_east: false,
            east: false,
            south_east: false,
            south: false,
            south_west: false,
            west: false,
            north_west: false
        },
        error: null
    }
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    CreateSpot(username, password) {
        return axios({
            method: "POST",
            url: "login",
            baseURL: this.domain,
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify({spot_name, desc, lat, lng, img, north, north_east, east, south_east, south, }),
        })
        .then((response)=> {
            this.setUser(response.data)
        })
    }

    handleFormSubmit = (e)=> {
        e.preventDefault();
        this.CreateSpot(this.state.spot.username, this.state.spot.password)
            .then(()=> {
                this.setState({error: "There was an error"});
                this.props.history.push("/");
            })
            .catch(({response})=> {
                this.setState({error: response.data.message});
            })
      }

      handleFormChange = (e)=> {
        let spot = {...this.state.spot}
        spot[e.target.name] = e.target.value 
        this.setState({ 
            spot: spot
        })
      }

    render(){
        return(
            <MainLayout>
                <h1>Hey Create</h1>
                <form className="FormContainer" onSubmit={this.handleFormSubmit}>
                    <div className="flexItem">
                        <label>Username:</label>
                        <input 
                            type="text" 
                            name="username" 
                            placeholder="username" 
                            value={this.state.username} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <label>Password:</label>
                        <input 
                            type="text" 
                            name="password" 
                            placeholder="Password" 
                            value={this.state.password} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <input type="submit" value="Submit" />
                    </div>

                </form>
            </MainLayout>
        )
    }
}

export default CreateSpot;
