import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import Fullscreen from "../Components/Fullscreen";
import axios from "axios";

class SurfSpotDetail extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount(){
        // Catching the params from the URL
        let params = this.props.match.params.id
        let URL  = `${process.env.REACT_APP_API}/marker/` + params; 
        //Great hack for making "this" work
        var self = this;
        axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    getWindDirections () {
        if (this.state.response.west === true) {
            return "WEST"
        } else {
             return "No West"
        }

        var expr = 'Papayas';
        switch (expr) {
          case 'Oranges':
            console.log('Oranges are $0.59 a pound.');
            break;
          case 'Mangoes':
          case 'Papayas':
            console.log('Mangoes and papayas are $2.79 a pound.');
            // expected output: "Mangoes and papayas are $2.79 a pound."
            break;
          default:
            console.log('Sorry, we are out of ' + expr + '.');
        }

    }

    render(){
        let windyURL = "https://www.windy.com/?" + this.state.response.lat + "," + this.state.response.lng;

        return(
            <div>
                <Fullscreen  
                    image={this.state.response.img} 
                />
                <MainLayout>
                    <h1>{this.state.response.spot_name}</h1>
                    <p>{this.state.response.desc}</p>
                    <h4>Winddirections:</h4>
                    {this.getWindDirections()}
                    <h2>Forecasts</h2>
                    <ul>
                        <li><a href={windyURL}>{this.state.response.spot_name} on Windy</a></li>
                    </ul>
                </MainLayout>  
            </div>
        )
    }
}



export default SurfSpotDetail;
