import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import Fullscreen from "../Components/Fullscreen";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from "axios";

class User extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount(){
        // Catching the params from the URL
        let params = this.props.match.params.id
        let URL  = `${process.env.REACT_APP_API}/user/` + params; 
        //Great hack for making "this" work
        var self = this;
        axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }


    render(){
        console.log(this.state.response)
        return(
                <MainLayout>
                    <h1>Hi {this.state.response.username}</h1>
                    <p> This is your profilepage, {this.state.response.firstname}. </p>
                    <Link to="/logout" > Logout </Link>
                </MainLayout>  

        )
    }
}



export default User;
