import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "../Css/BeerView.css";
import axios from "axios";
import BeerView from "./BeerView";


class BeerViewSingle extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount() {
        // Catching the params from the URL
        let params = this.props.match.params.id
        let URL = "https://ih-beers-api.herokuapp.com/beers/" + params;
        //Great hack for making "this" work
        var self = this;
        axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render() {
        let Beers = this.state.response.map((beer)=> {
            return(

                    <BeerView title={beer.name} 
                              description={beer.description}
                              image={beer.image_url}
                              created={beer.contributed_by}
                              link={`/beer-details/${beer._id}`}

                    />

            )})


        return(
            <div className="BeerView">
                {Beers}
            </div>
        )
    }
}

export default BeerViewSingle;
