import ReactMapGL, {Marker, Popup} from 'react-map-gl';
import MapMarker from "../Components/MapMarker"
// import MainLayout from '../Layout/MainLayout';
import Navbar from "../Components/Navbar";

class Map extends Component {

  state = {
    showPopup:{
        showPopup: true
    },
    viewport: {
      width: 100 + "%",
      height: 100 + "vh",
      latitude: 37.7577,
      longitude: -122.4376,
      zoom: 6
    }

  }

   handleClick(map, event) {
    console.log(event.this.lngLat)
  }

  render() {

    const TOKEN = "pk.eyJ1IjoibWFyaXVzZ3JvbmRhaGwiLCJhIjoiY2p6OHZpb2JpMDMwNTNkbzJlbnJyb2lkdiJ9.zTQFrIU4__uaAPGzAUQSFw";
    return (
        <Navbar>
            <ReactMapGL onDblClick={this.handleClick} mapboxApiAccessToken={TOKEN} 
                {...this.state.viewport}
                onViewportChange={(viewport) => this.setState({viewport})}
            >

                <Marker latitude={37.78} longitude={-122.41} offsetLeft={-20} offsetTop={-10}>
                <MapMarker title="Tittel på sted" desc="Lengre inngress om bla bla bla"/>
                </Marker>
                <Marker latitude={47.78} longitude={-122.41} offsetLeft={-20} offsetTop={-10}>
                <MapMarker title="Tittel på sted" desc="Lengre inngress om bla bla bla"/>
                </Marker>
                {this.state.showPopup && <Popup
                latitude={37.78}
                longitude={-122.41}
                closeButton={true}
                closeOnClick={false}
                onClose={() => this.setState({showPopup: false})}
                anchor="top" >

                </Popup>}
            </ReactMapGL>
      </Navbar>



    );
  }
}

export default Map;
