import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import Fullscreen from "../Components/Fullscreen";
import axios from "axios";
import qs from "querystring";

class CreateSpot extends Component {
    constructor(props){
        super(props);

    this.state = {
        spot: {
            spot_name: "", 
            desc: "", 
            lat: "",
            lng: " ",
            img: " ",
            north: false,
            north_east: false,
            east: false,
            south_east: false,
            south: false,
            south_west: false,
            west: false,
            north_west: false
        },
        error: null
    }
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    CreateSpot(spot_name, desc, lat, lng, img, north, north_east, east, south_east, south, south_west, west, north_west) {
        return axios({
            method: "POST",
            url: "marker/create",
            baseURL: process.env.REACT_APP_API,
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify({spot_name, desc, lat, lng, img, north, north_east, east, south_east, south, south_west, west, north_west}),
        })
        .then((response)=> {
           console.log("hei")
        })
    }

    handleFormSubmit = (e)=> {
        e.preventDefault();
        this.CreateSpot(this.state.spot.username, this.state.spot.password)
            .then(()=> {
                this.setState({error: "There was an error"});
                this.props.history.push("/");
            })
            .catch(({response})=> {
                this.setState({error: response.data.message});
            })
      }

      handleFormChange = (e)=> {
        let spot = {...this.state.spot}
        spot[e.target.name] = e.target.value 
        this.setState({ 
            spot: spot
        })
      }

    render(){
        return(
            <MainLayout>
                <h1>Add a new spot</h1>
                <form className="FormContainer" onSubmit={this.handleFormSubmit}>
                    <div className="flexItem">
                        <label>Spotname</label>
                        <input 
                            type="text" 
                            name="spot_name" 
                            placeholder="Name of surfspot" 
                            value={this.state.spot_name} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <label>Description</label>
                        <textarea 
                            type="text-field" 
                            name="desc" 
                            placeholder="Describe the spot" 
                            value={this.state.desc} 
                            onChange={this.handleFormChange}>
                        </textarea>
                    </div>

                    <div className="flexItem">
                        <label>Latitude</label>
                        <input 
                            type="text" 
                            name="lat" 
                            placeholder="Latitude" 
                            value={this.state.lat} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <label>Longitude</label>
                        <input 
                            type="text" 
                            name="lng" 
                            placeholder="Longitude" 
                            value={this.state.lng} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <label>ImageURL</label>
                        <p>Paste an imageadress </p>
                        <input 
                            type="text" 
                            name="lng" 
                            placeholder="Longitude" 
                            value={this.state.lng} 
                            onChange={this.handleFormChange}>
                        </input>
                    </div>

                    <div className="flexItem">
                        <input type="submit" value="Submit" />
                    </div>
                   img, north, north_east, east, south_east, south, south_west, west, north_west
                </form>
            </MainLayout>
        )
    }
}

export default CreateSpot;
