import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import axios from "axios";

class SurfSpotDetail extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount(){
        // Catching the params from the URL
        let params = this.props.match.params.id
        let MarkerURL  = `${process.env.REACT_APP_API}/marker/all-markers`; 
        let URL = "https://ih-beers-api.herokuapp.com/beers/" + params;
        //Great hack for making "this" work
        var self = this;
        axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render(){
        return(
            <MainLayout>
            <h1>Spotdetail</h1>
            </MainLayout>  
        )
    }
}



export default SurfSpotDetail;
