import React, { Component } from 'react';
import MainLayout from "../Layout/MainLayout";
import Fullscreen from "../Components/Fullscreen";
import axios from "axios";

class SurfSpotDetail extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            response: [],
        };
      }

    componentDidMount(){
        // Catching the params from the URL
        let params = this.props.match.params.id
        let URL  = `${process.env.REACT_APP_API}/marker/` + params; 
        //Great hack for making "this" work
        var self = this;
        axios.get(URL)
            .then(function (response){
                self.setState({response: response.data})
            })
            .catch(function (error) {
                return error;
            });
    }

    render(){
        console.log(this.state.response.spot_name)
        return(
            <MainLayout>
                <Fullscreen  img={this.state.response.img}/>
            <h1>{this.state.response.spot_name}</h1>
            <h1>{this.state.response._id}</h1>
            </MainLayout>  
        )
    }
}



export default SurfSpotDetail;
