        })

        let SideBar = this.state.countries.map((country, index)=> {
            return(
              <li><Link to={`/details/${country.cca3}`}><span>{country.flag}</span>{country.name.common}</Link></li>
            )})

        return (
            <div>
                <h1>Welcom to all beers</h1>
                {Beers}
            </div>
        )
    }
}

export default Beer;
